
public class Declaraciones_Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		
		int b;
		
		
		double d=Math.PI;
		
		System.out.println("pi:"+d);
		b=7;
		
		float c=b+a;
		c++;
		c-=2;//decrementar en 2 a c
		
		c+=6;//incrementar en 6 a c
		
		c= c/5;
		
		System.out.println("suma:"+c);
		

	}

}
